results = set()

regex = re.compile(r"\{Binding (.\S+)[,}=]")

def parse_file(path: Path):
    with path.open() as f:
        all_lines = f.readlines()
        for line in all_lines:
            match = regex.search(line)
            if match:
                try:
                    res = match.group(1)
                    results.add(res)
                except IndexError as e:
                    pass

for p in xaml_paths:
    parse_file(p)

for el in sorted(results):
    print(el)

with (Path(__file__).parent / "XAMLBindings.txt").open("w") as f:
    for line in sorted(results):
        f.write(line)
        f.write('\n')